import React from 'react';
import './App.css';


class TextWithCheck extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            txt: this.props.txt
        }

        this.update = this.state.update.bind(this);
    }

    update(e) {
        this.setState({
            txt: e.target.value,
        });
    }

    render() {
        return (
            <div className="textWithCheck">
                <input type="checkbox"></input>

                <input type="text" value={this.state.txt} onChange={this.update}></input>
                <h1>{this.state.txt}</h1>
            </div>);
    }
}

