class App extends React.Component {

    constructor() {
        super();
        this.state = {
            todos: [
                {
                    id: 1,
                    title: "let it be x"

                },
                {
                    id: 2,
                    title: "let it be y"

                },

            ]

        }

        this.deleteTodo = this.deleteTodo.bind(this);

    }


    addTodo(e) {
        let inputValue = e;
        this.setState((prevState, props) => {
            return {
                todos: [...prevState.todos, {
                    id: prevState.todos.length > 0 ? prevState.todos[prevState.todos.length - 1].id + 1 : 1,
                    title: inputValue
                }]

            }
        });
    }

    deleteTodo(index) {
        this.setState((prevState, props) => {
                let newTodos = prevState.todos;

                newTodos.splice(index, 1);

                console.log("csc");
                return {todos: newTodos}

            }
        );
    }

    render() {
        return (
            <div>
                <TitleField addTodo={this.addTodo.bind(this)}/>
                {this.state.todos.map((todo, index) =>
                    <div key={todo.id.toString()}>
                        <TextWithCheck todo={todo} deleteTodo={() => {
                            this.deleteTodo(index)
                        }}/>
                    </div>
                )}
            </div>
        );
    }
}

