class App extends React.Component {

    constructor() {
        super();
        this.state = {
            todos: [],

            checkList: []

        }

        this.deleteTodo = this.deleteTodo.bind(this);

    }


    addTodo(e) {
        let inputValue = e;
        this.setState((prevState, props) => {
            return {
                todos: [...prevState.todos, {
                    id: prevState.todos.length > 0 ? prevState.todos[prevState.todos.length - 1].id + 1 : 1,
                    title: inputValue
                },
                ],
                checkList: [prevState.checkList, false]

            }
        });
    }

    deleteTodo(index) {
        this.setState((prevState, props) => {
                let newTodos = prevState.todos;

                newTodos.splice(index, 1);

                console.log("csc");
                return {todos: newTodos}

            }
        );
    }

    isCheck = (todo) => {
        if (this.state.checkList.indexOf(todo) !== -1) {
            return true;
        } else {
            return false;
        }
    };

    addCheck = (index) => {

        this.setState((prevState, props) => {
            let checks = prevState.checkList;
            checks[index] = true;
            return {checkList: checks};
        });
    }

    render() {
        return (
            <div>
                <TitleField addTodo={this.addTodo.bind(this)}/>
                {this.state.todos.map((todo, index) =>
                    <div key={todo.id.toString()}>
                        <TextWithCheck todo={todo} deleteTodo={() => {
                            this.deleteTodo(index)
                        }}
                                       isCheck={this.isCheck(todo)}

                                       addCheck={() => this.addCheck(index)}

                        />
                    </div>
                )}
            </div>
        );
    }
}

