class TitleField extends React.Component {

    constructor(props) {
        super(props);
        this.state = {txt: ''};
    }

    update(e) {
        this.setState({txt: e.target.value})
    }

    clearField = () => {

        console.log('dfghj');

        this.setState({
            txt: ""
        });
    };

    render() {
        return (<div>

            <input type="text" placeholder="Enter the Title for your Todo" value={this.state.txt}
                   onChange={this.update.bind(this)}/>

            <Button actionPerform={() => {
                this.props.addTodo(this.state.txt);
                this.clearField();

            }} text={"add"}/>

        </div>);

    }

}


var TextWithCheck = (props) => <div className="textWithCheck">
