import React from 'react';
import './App.css';


class App extends React.Component {
    constructor() {
        super();
        this.state = {
            currentevent: "------"
        }
        this.update = this.update.bind(this)
    }

    update(e) {
        this.setState(
            {
                currentevent: e.type
            }
        )
    }

    render() {
        return (<div>
           <textarea className="shushant"
               cols="30"
               rows="10"
               onKeyPress={this.update}
           />   <h1>{this.state.currentevent}</h1>

        </div>)
    }
}

export default App;
