import React from 'react';
import './App.css';


class App extends React.Component {
    constructor() {
        super();
        this.state = {
            txt: "hello here"
        }
    }

    update(e) {
        this.setState(
            {
                txt: e.target.value
            }
        )
    }

    render() {
        return (<div>
            <input type= "text"  onChange={this.update.bind(this)}></input>
            <h1>{this.state.txt}</h1>

        </div>)
    }
}

App.protoTypes = {
    txt: React.propTypes,
}
