import React from 'react';
import './App.css';


class App extends React.Component {
    constructor() {
        super();
        this.state = {
            currentevent: "------"
        }
     this.update = this.update.bind(this)
    }

    update(e) {
        this.setState(
            {
                currentevent: e.type
            }
        )
    }

    render() {
        return (<div>
            <input type= "text"  onChange={this.update.bind(this)}></input>
            <h1>{this.state.txt}</h1>

        </div>)
    }
}


App.protoTypes = {
    txt: React.propTypes,
}


