import React from 'react';
import './App.css';


class TextWithCheck extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            txt: this.props.txt
        }

        this.update = this.update.bind(this);
    }

    update(e) {
        this.setState({
            txt: e.target.value,
        });
    }

    render() {
        return (
            <div className="textWithCheck">
                <input type="checkbox"></input>

                <input type="text" value={this.state.txt} onChange={this.update}></input>
                <h1>{this.state.txt}</h1>
            </div>);
    }
}

class App extends React.Component {

    constructor() {
        super();
        this.state = {
            numb: ["1", "2", "3"]
        }
        this.update = this.update.bind(this);
    }

    update(e) {
        this.setState({
            numb: this.state.numb.add("4"),
        });
    }

    render() {
        return (
            <div className="bodyType">
                {Header}
                <Button val={this.update.bind(this)}/>
                <NumberList numb={this.state.numb}/>
            </div>);
    }
}

function NumberList(props) {
    const numb = props.numb;

    const listItems = numb.map((n) =>
        <li>
            <TextWithCheck children={n}/>
        </li>
    );

    return (
        <ul>{listItems}</ul>
    );
}

class Button extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            val: this.props.val
        }
        this.update = this.update.bind(this);
    }

    update(e) {
        this.setState({
            val: this.val + 1,
        });
    }

    render() {
        return (
            <div className="buttonType">
                <button onChange={this.update}>
                    add
                </button>
            </div>);
    }
}

var Header = <header>

</header>


export default App;
