    addTodo(e) {
        if (e.key === 'Enter') {
            let inputValue = e.target.value;
            this.setState((prevState, props) => {

                return {
                    todos: [...prevState.todos, {
                        id: prevState.todos[prevState.todos.length - 1].id + 1,
                        title: inputValue
                    }]

                }
            });
        }


    }

    deleteTodo(e) {
        this.setState((prevState, props) => {

                let newTodos = prevState.todos.pop();
                return {todos: newTodos}

            }
        )
        ;
    }

    render() {
        return (
            <div>
                <TitleField addTodo={this.addTodo.bind(this)}/>
                {this.state.todos.map((todo, index) => <div key={todo.id.toString()}><TextWithCheck todo={todo}
                                                                                                    deleteTodo={this.deleteTodo.bind(this)}/>
                </div>)}
            </div>
        );
    }
}

