class App extends React.Component {

    constructor() {
        super();
        this.state = {
            todos: localStorage.getItem('todos'),
            count: 0,
            checkList: [],
        };


        this.deleteTodo = this.deleteTodo.bind(this);

    }

    getTodos = () => {
        let todos = this.state.todos;

        const completedTodos = todos.filter(function (element) {
            return element.completed === true;
        });
        const inCompletedTodos = todos.filter(function (element) {
            return element.completed === false;
        });

        const returnTodo = [];

        returnTodo.push.apply(returnTodo, inCompletedTodos);

        returnTodo.push.apply(returnTodo, completedTodos);


        return returnTodo;
    };

    addTodo(e) {
        let inputValue = e;
        this.setState((prevState, props) => {
            return {
                todos: [...prevState.todos, {
                    id: prevState.count + 1,
                    title: inputValue,
                    completed: false,
                },
                ],
                count: prevState.count + 1,
                checkList: [prevState.checkList, false]

            }
        });

        localStorage.setItem('todos', JSON.stringify(todos));

    }

    deleteTodo(id) {
        this.setState((prevState, props) => {
                let newTodos = prevState.todos;

                newTodos.splice(newTodos.indexOf(newTodos.find(todo => todo.id === id)), 1);

                console.log("csc");
                return {todos: newTodos}

            }
        );
    }


    addCheck = (id) => {
        console.log("alpha");
        this.setState((prevState, props) => {
            let checks = prevState.todos;
            checks.find(todo => todo.id === id).completed = true;
            return {todos: checks};
        });
    };

    render() {
        return (
            <div>
                <TitleField addTodo={this.addTodo.bind(this)}/>
                {this.getTodos().map((todo, index) =>
                    <div key={todo.id.toString()}>
                        <TextWithCheck todo={todo} deleteTodo={() => {
                            this.deleteTodo(todo.id)
                        }}
                                       isCheck={todo.completed}

                                       addCheck={() => this.addCheck(todo.id)}

                        />
                    </div>
                )}
            </div>
        );
    }
}

