import React from 'react';
import './App.css';


class TextWithCheck extends React.Component {

    constructor() {
        super();
        this.state = {
            txt: ' '
        }
        this.update = this.update.bind(this);
    }

    update(e) {
        this.setState({
            txt: e.target.value,
        });
    }

    render() {
        return (
            <div className="textWithCheck">

                <input type="checkbox"></input>

                <input type="text" value={this.state.txt} onChange={this.update}></input>
                <h1>{this.state.txt}</h1>
            </div>);
    }
}

class Button extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            val: 1
        }
        this.update = this.update.bind(this);
    }

    update(e) {
        this.setState({
            val: this.val + 1,
        });
    }

    render() {
        return (
            <div className="buttonType">
                <button onChange={this.update}>
                    add
                </button>
            </div>);
    }
}

var Header = <header>

</header>

function App() {
    return (
        <div className="bodyType">
            {Header}
            {Button}
            <TextWithCheck/>
            <TextWithCheck/>
            <TextWithCheck/>
        </div>)
}


export default App;
